      //checkLocalForToken()
      console.log('effect')
    }, [])

  return (
    <div>
    {(user === null) ? 
      <Login 
        password={password}
        setPassword={setPassword}
        password={password} 
        setUsername={setUsername}
        handleLogin={handleLogin} /> : <BlogList name={name} />}
    </div>
  )

}

const BlogList = ({name}) => {
  const handleLogout = () => {
    setUser(null)
  }
  return (
    <div>
      <h1>Blogs</h1>
      <p>{name}</p> <button onClick={() => handleLogout}> Logout</button>
    </div>
  )
}

const Login = ({
  password, 
  setPassword, 
  username,
  setUsername, 
  handleLogin
}) => {

  return (
    <div>
      <h1>Login in Application</h1>
        <form onSubmit={handleLogin}>
          <div>
            username 
              <input 
                type="text"
                value={username}
                name="Username"
                onChange={({target}) => {setUsername(target.value)}}
              />
          </div>
          <div>
            password
            <input 
              type="password"
              value={password}
              name="Password"
              onChange={({ target }) => {setPassword(target.value)}}
            />
          </div>
        <button>Login</button>
      </form>
    </div>
  );
}

export default App;
