import axios from 'axios'

const tokenCheck = () => {
    const localToken = window.localStorage('userToken')

    if(localToken)  {
        return localToken
    }
    return null
}


const login = async credentials => {
    const resposne = await axios.post(baseUrl, credentials)
    return resposne.data
} 

export default {login, tokenCheck}
